import axios from 'axios';

const fetchPizzas = () => (dispatch) => {
  dispatch(setLoaded);
  axios.get('http://localhost:3001/pizzas').then(({ data }) => {
    dispatch(setPizzas(data));
  });
};

const setLoaded = (payload) => ({
  type: 'SET_LOADED',
  payload,
});

const setPizzas = (items) => ({
  type: 'SET_PIZZAS',
  payload: items,
});

export { setPizzas, fetchPizzas };
