import React from 'react';
import * as yup from 'yup';
import { Formik } from 'formik';
import { useDispatch, useSelector } from 'react-redux';
import { useForm } from 'react-hook-form';

import { signUpUser, signUpUserSuccess, signUpUserFailure } from '../redux/actions/user';
import { useHistory } from 'react-router-dom';
import { TabAuth } from '../components';

export default function SingUp() {
  const dispatch = useDispatch();
  const { register, handleSubmit, errors } = useForm();
  const { error, isAuth, loading } = useSelector(({ userReducer }) => userReducer);

  const history = useHistory();

  const onSubmit = (data) => {
    console.log('Clicked ', loading);
    console.log(data);

    // dispatch(signUpUser(values))
    //   .then(({ data }) => {
    //     setTimeout(() => {
    //       localStorage.setItem('token', JSON.stringify(data.auth_token));
    //       dispatch(signUpUserSuccess(data));
    //       history.push('./account');
    //     }, 3000);
    //   })
    //   .catch((error) => {
    //     dispatch(signUpUserFailure(error.message));
    //   });
  };

  return (
    <div className="container">
      <form onSubmit={handleSubmit(onSubmit)}>
        <div className="form-control">
          <label>Username</label>
          <input
            type="text"
            name="username"
            ref={register({ required: 'Username is required.' })}
          />
          {errors.username && <p className="error"> {errors.username.message} </p>}
        </div>
        <div className="form-control">
          <label>Фамилия</label>
          <input type="text" name="last_name" ref={register({ required: true })} />
          {errors.last_name && <p className="error">last_name is required.</p>}
        </div>
        <div className="form-control ">
          <label>Email</label>
          <input
            type="text"
            name="email"
            ref={register({
              required: true,
              pattern: /^[^@ ]+@[^@ ]+\.[^@ .]{2,}$/,
            })}
          />
          {errors.email && errors.email.type === 'required' && (
            <p className="error">Email is required.</p>
          )}
          {errors.email && errors.email.type === 'pattern' && (
            <p className="error">Email is not valid.</p>
          )}
        </div>
        <div className="form-control">
          <label>Password</label>
          <input type="password" name="password" ref={register({ required: true, minLength: 6 })} />
          {errors.password && errors.password.type === 'required' && (
            <p className="error">Password is required.</p>
          )}
          {errors.password && errors.password.type === 'minLength' && (
            <p className="error">Password should be at-least 6 characters.</p>
          )}
        </div>
        <div className="form-control">
          <label></label>
          <button type="submit">Регистрация</button>
        </div>
      </form>
    </div>
  );
}
