const initialState = {
  items: {},
  totalPrice: 0,
  itemCount: 0,
};

function cart(state = initialState, action) {
  if (action.type === 'SET_TOTALPRICE') {
    return {
      ...state,
      totalPrice: action.payload,
    };
  }

  if (action.type === 'SET_COUNT') {
    return {
      ...state,
      itemCount: action.payload,
    };
  }

  switch (action.payload) {
    case '':
      return {
        ...state,
        items: {
          [action.payload.id]: {},
        },
      };
    default:
      break;
  }
  return state;
}

export default cart;
