const initialState = {
  currentUser: {},
  status: null,
};

function registration(state = initialState, action) {
  switch (action.type) {
    case 'REGISTRATION_USER':
      return {
        ...state,
        currentUser: action.payload,
        status: 'authenticated',
      };
    case 'ME_FROM_TOKEN':
      return {
        ...state,
        currentUser: action.payload,
        status: 'authenticated',
      };
    case 'LOGIN_USER':
      return {
        ...state,
        currentUser: action.payload,
        status: 'authenticated',
      };

    case 'LOGOUT_USER':
      return {
        ...state,
        currentUser: action.payload,
        status: null,
      };

    case 'EDIT_USER':
      return {
        ...state,
        currentUser: action.payload,
        status: 'authenticated',
      };
    default:
      return state;
  }
}

export default registration;
