import React from 'react';
import * as yup from 'yup';
import { Formik } from 'formik';
import { useDispatch, useSelector } from 'react-redux';
import { Redirect } from 'react-router-dom';

import { fetchUser } from '../redux/actions/registration';

function LoginPopup() {
  const dispatch = useDispatch();
  const currentUser = useSelector(({ registration }) => registration.currentUser);

  const validationSchema = yup.object().shape({
    email: yup.string().email('Введите коректный Email').required('Обезятельно'),
    password: yup.string().required('Обезятельно'),
  });

  const onHandleSumbit = (values) => {
    dispatch(fetchUser(values));
  };

  return (
    <div>
      <Formik
        initialValues={{
          email: '',
          password: '',
        }}
        validateOnBlur
        validationSchema={validationSchema}
        onSubmit={(values) => onHandleSumbit(values)}>
        {({ values, errors, touched, handleBlur, handleChange, handleSubmit, isValid, dirty }) => (
          <div className="form">
            <p>Email</p>
            <input
              name="email"
              type="email"
              onChange={handleChange}
              onBlur={handleBlur}
              value={values.email}
            />
            <br />
            {touched.email && errors.email && <p className="error">{errors.email}</p>}
            <p>Пароль </p>
            <input
              name="password"
              type="password"
              onChange={handleChange}
              onBlur={handleBlur}
              value={values.password}
            />
            {touched.password && errors.password && <p className="error">{errors.password}</p>}

            <button disabled={!isValid && !dirty} onClick={handleSubmit} type="submit">
              Войти
            </button>
            <p>
              Вы у нас впервые
              <Link key={2} to="/registration">
                Регисрация
              </Link>
            </p>
          </div>
        )}
      </Formik>
    </div>
  );
}

export default LoginPopup;
