import React from 'react';
import { useDispatch } from 'react-redux';
import { fetchUser } from '../redux/actions/registration';

function Registration() {
  const dispatch = useDispatch();
  const initialState = {
    password: '',
    username: '',
  };
  const [state, setState] = React.useState(initialState);

  const onSumbitHandler = (event) => {
    event.preventDefault();
  };

  const handleChange = (event) => {
    setState({ [event.target.name]: event.target.value, [event.target.name]: event.target.value });
  };

  const loginHandler = () => {};
  const regisHandler = (event) => {
    console.log(state);
    // dispatch(fetchUser(state));
  };

  return (
    <div style={{ textAlign: 'center' }}>
      <form onSubmit={onSumbitHandler}>
        <input
          value={state.username}
          name="username"
          placeholder="Username"
          onChange={handleChange}
        />
        <br />
        {/* <input value={state.password} name="password" placeholder="Email" onChange={handleChange} /> */}
        <br />
        <button onClick={loginHandler}>Войти</button>
        <button onClick={regisHandler}>Регисрация</button>
      </form>
    </div>
  );
}

export default Registration;
