const initialState = {
  currentUser: null,
  status: null,
  error: null,
  loading: false,
};

function registration(state = initialState, action) {
  switch (action.type) {
    case 'REGISTRATION_USER':
      return {
        ...state,
        currentUser: action.payload,
        status: 'authenticated',
        isAuth: true,
      };
    case 'ME_FROM_TOKEN':
      return {
        ...state,
        currentUser: action.payload,
        status: 'authenticated',
        isAuth: true,
      };
    case 'ME_FROM_TOKEN':
      return {
        ...state,
        currentUser: action.payload,
        status: 'authenticated',
        isAuth: true,
      };
    case 'ME_FROM_TOKEN':
      return {
        ...state,
        currentUser: action.payload,
        status: 'authenticated',
        isAuth: true,
      };
    case 'LOGIN_USER':
      return {
        ...state,
        currentUser: action.payload,
        status: 'authenticated',
        isAuth: true,
      };

    case 'LOGOUT_USER':
      return {
        ...state,
        currentUser: action.payload,
        status: 'logout',
        isAuth: false,
      };

    case 'EDIT_USER':
      return {
        ...state,
        currentUser: action.payload,
      };
    default:
      return state;
  }
}

export default registration;
