const initialState = {
  currentUser: {},
  error: null,
  loading: false,
  isAuth: !!localStorage.getItem('token'),
};

function registration(state = initialState, action) {
  switch (action.type) {
    case 'REGISTRATION_USER':
      return {
        ...state,
        currentUser: action.payload,
        isAuth: true,
      };
    case 'ME_FROM_TOKEN':
      return {
        ...state,
        loading: true,
      };
    case 'ME_FROM_TOKEN_SUCCESS':
      return {
        ...state,
        currentUser: action.payload,
        error: null,
        loading: false,
        isAuth: true,
      };
    case 'ME_FROM_TOKEN_FAILURE':
      return {
        ...state,
        error: action.payload,
        loading: false,
        isAuth: false,
      };
    case 'LOGIN_USER':
      return {
        ...state,
        currentUser: action.payload,
        loading: false,
        isAuth: true,
      };

    case 'LOGOUT_USER':
      return {
        ...state,
        currentUser: action.payload,
        isAuth: false,
      };

    case 'EDIT_USER':
      return {
        ...state,
        currentUser: action.payload,
      };
    default:
      return state;
  }
}

export default registration;
