function addCartPizza(pizzaObj) {
  return {
    type: 'ADD_PIZZAS',
    payload: pizzaObj,
  };
}

function clearCart() {
  return {
    type: 'CLEAR_CART',
  };
}

function removeCartItem() {
  return {
    type: 'REMOVE_CART_ITEM',
  };
}

export { addCartPizza, clearCart, removeCartItem };
