    //         <Button className="button--cart">
    //           {/* <span>{totalPrice} USD</span> */}
    //           {/* <div className="button__delimiter"></div> */}
    //           <img src={logo} />
    //           <span>{itemCount}</span>
    //         </Button>
    //       </Link>
    //       <LoginPopup />
    //     </div>
    //   </div>
    // </div>

    <header class="header">
      <div class="container">
        <nav class="nav">
          <Link to="/" class="nav__logo">
            <img src={logo} alt="" />
          </Link>
          <div class="categoties">
            <ul class="categories__list">
              <li class="categories__link active">
                <a href="#">Мужское</a>
              </li>
              <li class="categories__link">
                <a href="#">Женское</a>
              </li>
              <li class="categories__link">
                <a href="#">Детское</a>
              </li>
            </ul>
          </div>
          <div class="nav__control">
            <button class="search-button">
              <img src={searchImg} alt="" />
            </button>
            <Link to="/cart" class="cart-button">
              <img src={cartImg} alt="" />
              <span class="cart-count">{itemCount}</span>
            </Link>
            <LoginPopup />
          </div>
        </nav>
      </div>
    </header>
  );
}

export default Header;
