import { array } from 'prop-types';

const initialState = {
  items: {},
  totalPrice: 0,
  itemCount: 0,
};

function cart(state = initialState, action) {
  if (action.type === 'SET_TOTALPRICE') {
    return {
      ...state,
      totalPrice: action.payload,
    };
  }

  if (action.type === 'SET_COUNT') {
    return {
      ...state,
      itemCount: action.payload,
    };
  }

  switch (action.type) {
    case 'ADD_PIZZAS':
      const currentPizzaItems = !state.items[action.payload.id]
        ? [action.payload]
        : [...state.items[action.payload.id], action.payload];

      const newItems = {
        ...state.items,
        [action.payload.id]: {
          items: currentPizzaItems,
          totalPrice: currentPizzaItems.reduce((sum, obj) => obj.price + sum, 0),
        },
      };
      const totalArr = [].concat.apply([], Object.values(currentPizzaItems));

      return {
        ...state,
        items: newItems,
        itemCount: totalArr.length,
        totalPrice: totalArr.reduce((sum, obj) => obj.price + sum, 0),
      };
    default:
      return state;
  }
}

export default cart;
