import React from 'react';
import { Catigories, PizzaBlock, SortPopup } from '../components';
import PropTypes from 'prop-types';

function Home({ pizzas }) {
  return (
    <div className="container">
      <div className="content__top">
        <Catigories items={['Мясные', 'Вегетарианская', 'Гриль', 'Острые', 'Закрытые']} />
        <SortPopup
          items={[
            { name: 'популярности', type: 'popular' },
            { name: 'цене', type: 'price' },
            { name: 'алфавиту', type: 'alphabet' },
          ]}
        />
      </div>
      <h2 className="content__title">Все пиццы</h2>
      <div className="content__items">
        {pizzas && pizzas.map((item) => <PizzaBlock key={item.id} {...item} />)}
      </div>
    </div>
  );
}

Home.propTypes = {
  pizzas: PropTypes.arrayOf(PropTypes.object).isRequired,
};

export default Home;
