const initialState = {
  currentUser: {},
};

function registration(state = initialState, action) {
  switch (action.type) {
    case 'REGISTRATION_USER':
      return {
        ...state,
        currentUser: action.payload,
      };

    case 'ME_FROM_TOKEN':
      return {
        ...state,
        user: action.payload.data.user,
        status: 'authenticated',
        error: null,
        loading: false,
      };
    default:
      return state;
  }
}

export default registration;
