import React from 'react';
import * as yup from 'yup';
import { Formik } from 'formik';
import { Link, Redirect } from 'react-router-dom';

import { useDispatch, useSelector } from 'react-redux';
import { useHistory } from 'react-router-dom';

// import { fetchUser } from '../redux/actions/registration';
import { logoutUser } from '../redux/actions/logout';
import { loginUser } from '../redux/actions/login';

function LoginPopup() {
  const currentUser = useSelector(({ registration }) => registration.currentUser);
  const dispatch = useDispatch();
  const history = useHistory();
  const validationSchema = yup.object().shape({
    email: yup.string().email('Введите коректный Email').required('Обезятельно'),
    password: yup.string().required('Обезятельно'),
  });

  const user = !Object.keys(currentUser).length == 0;

  const onHandleSumbit = (values) => {
    // dispatch(loginUser(values))
    console.log('Login');
  };

  const logoutButton = () => {
    localStorage.removeItem('token');
    dispatch(logoutUser());
    history.push('/');
  };

  const [visiblePopup, setVisiblePopup] = React.useState(false);
  const loginRef = React.useRef();

  const toggleVisiblePopup = () => {
    setVisiblePopup(!visiblePopup);
  };

  const handleOutsideClick = (event) => {
    if (!event.path.includes(loginRef.current)) {
      setVisiblePopup(false);
    }
  };

  return (
    <div className="popup" ref={loginRef}>
      {!user ? (
        <Formik
          initialValues={{
            email: '',
            password: '',
          }}
          validateOnBlur
          validationSchema={validationSchema}
          onSubmit={(values) => onHandleSumbit(values)}>
          {({
            values,
            errors,
            touched,
            handleBlur,
            handleChange,
            handleSubmit,
            isValid,
            dirty,
          }) => (
            <div className="popup__list">
              <p>Email</p>
              <input
                name="email"
                type="email"
                onChange={handleChange}
                onBlur={handleBlur}
                value={values.email}
              />
              <br />
              {touched.email && errors.email && <p className="error">{errors.email}</p>}
              <p>Пароль </p>
              <input
                name="password"
                type="password"
                onChange={handleChange}
                onBlur={handleBlur}
                value={values.password}
              />
              {touched.password && errors.password && <p className="error">{errors.password}</p>}

              <button disabled={!isValid && !dirty} onClick={handleSubmit} type="submit">
                Войти
              </button>
              <p>
                Впервые у нас? <Link to="registration">Зарегистрироваться</Link>
              </p>
            </div>
          )}
        </Formik>
      ) : (
        <div>
          <div>Вы в системе</div>
          <button onClick={logoutButton}> Выйти </button>
        </div>
      )}
    </div>
  );
}

export default LoginPopup;
