import React from 'react';
import { Link } from 'react-router-dom';

const TabAuth = () => {
  // $(document).ready(function(){
  //   if(window.location.hash == '#qwerty'){
  //     $('.blockclass').addClass('class');
  //   }
  // });

  document.addEventListener('DOMContentLoaded', function () {
    if (window.location.href.indexOf('/signup')) {
      document.querySelector('.login').classList.remove('active');
      document.querySelector('.signup').classList.add('active');
    } else if (window.location.href.indexOf('/login')) {
      document.querySelector('.signup').classList.remove('active');
      document.querySelector('.login').classList.add('active');
    }
  });

  return (
    <div className="auth-tab">
      <Link className="auth-tab__link signup" to="/signup">
        Регистрация
      </Link>
      <Link className="auth-tab__link login" to="/login">
        Авторизация
      </Link>
    </div>
  );
};

export default TabAuth;
