import React from 'react';
import * as yup from 'yup';
import { Formik } from 'formik';

import { useSelector, useDispatch } from 'react-redux';

import { editUser, editUserFailure, editUserSuccess } from '../redux/actions/user';
import { useHistory } from 'react-router-dom';

function Edit() {
  const { loading, isAuth, user } = useSelector(({ userReducer }) => userReducer);
  const dispatch = useDispatch();
  const history = useHistory();

  const validationSchema = yup.object().shape({
    username: yup.string().required('Обезятельно'),
    last_name: yup.string().required('Обезятельно'),
    password: yup.string().required('Обезятельно'),
    email: yup.string().email('Введите коректный Email').required('Обезятельно'),
  });

  const onSave = (values, id) => {
    const token = JSON.parse(localStorage.getItem('token'));
    dispatch(editUser(values, id, token))
      .then(({ data }) => {
        dispatch(editUserSuccess(data));
        history.push('/account');
        console.log(data);
      })
      .catch((err) => {
        dispatch(editUserFailure(err.message));
      });
  };

  return (
    <div className="container">
      <h3 style={{ marginBottom: '20px' }}>Редоктировать </h3>
      {loading ? (
        <div>Loading...</div>
      ) : (
        <Formik
          initialValues={{
            username: user.username,
            last_name: user.last_name,
            email: user.email,
            password: 'fsafafa',
          }}
          validateOnBlur
          validationSchema={validationSchema}
          onSubmit={(values) => onSave(values, user.id)}>
          {({
            values,
            errors,
            touched,
            handleBlur,
            handleChange,
            handleSubmit,
            isValid,
            dirty,
          }) => (
            <div className="" style={{ display: 'flex' }}>
              <div className="form">
                <p>Имя</p>
                <input
                  name="username"
                  type="text"
                  onChange={handleChange}
                  onBlur={handleBlur}
                  value={values.username}
                />
                <br />
                {touched.username && errors.username && <p className="error">{errors.username}</p>}
                <br />
                <p>Фамилия </p>
                <input
                  name="last_name"
                  type="text"
                  onChange={handleChange}
                  onBlur={handleBlur}
                  value={values.last_name}
                />{' '}
                <br />
                {touched.last_name && errors.last_name && (
                  <p className="error">{errors.last_name}</p>
                )}
                <p>Email</p>
                <input
                  name="email"
                  type="email"
                  onChange={handleChange}
                  onBlur={handleBlur}
                  value={values.email}
                />
                <br />
                {touched.email && errors.email && <p className="error">{errors.email}</p>}
                <button disabled={!isValid && !dirty} onClick={handleSubmit} type="submit">
                  Сохранить
                </button>
              </div>

              <div className="change-password">
                <p>Пароль </p>
                <input
                  name="password"
                  type="password"
                  onChange={handleChange}
                  onBlur={handleBlur}
                  value={values.password}
                />
                {touched.password && errors.password && <p className="error">{errors.password}</p>}
              </div>
            </div>
          )}
        </Formik>
      )}
    </div>
  );
}

export default Edit;
