import React from 'react';
import { Formik } from 'formik';
import * as yup from 'yup';
import { useForm } from 'react-hook-form';

import { useSelector, useDispatch } from 'react-redux';
import { loginUser, loginUserFailure, loginUserSuccess } from '../redux/actions/user';
import { Link, Redirect, useHistory } from 'react-router-dom';
import { TabAuth } from '../components';

function Login() {
  const { error, isAuth, loading } = useSelector(({ userReducer }) => userReducer);
  const { register, errors, handleSubmit } = useForm();
  const dispatch = useDispatch();

  const history = useHistory();

  // const validationSchema = yup.object().shape({
  //   username: yup.string().required('Обезятельно'),
  //   password: yup.string().required('Обезятельно'),
  // });

  const onSumbit = (values) => {
    dispatch(loginUser(values))
      .then(({ data }) => {
        localStorage.setItem('token', JSON.stringify(data.auth_token));
        dispatch(loginUserSuccess(data));
        history.push('/account');
      })
      .catch((err) => {
        dispatch(loginUserFailure(err.message));
      });
  };

  return (
    <div className="container">
      <TabAuth />
      <form onSubmit={handleSubmit(onSumbit)}>
        <div className="form-control">
          <label>Username</label>
          <input
            type="text"
            name="username"
            ref={register({
              required: 'Username is required.',
            })}
          />
          {errors.username && <p className="error"> {errors.username.message} </p>}
        </div>

        <div className="form-control">
          <label>Password</label>
          <input
            type="password"
            name="password"
            ref={register({
              required: 'Password is required.',
              minLength: {
                value: 6,
                message: 'Password should be at-least 6 characters.',
              },
            })}
          />
          {errors.password && <p className="error"> {errors.password.message} </p>}
        </div>
        <div className="form-control">
          <label></label>
          <button type="submit">Войти</button>
        </div>
      </form>
    </div>
  );
}

export default Login;
