import axios from 'axios';

const fetchUser = (user) => (dispatch) => {
  axios.post(`https://jastegri.pythonanywhere.com/auth/users/`, user).then((res) => {
    // localStorage.setItem('token', data.jwt);
    console.log(res);
    if (res.status === 201) {
      dispatch(registrationUser(res.data));
    } else if (res.status >= 400) {
      console.log('Ошибка');
    }
  });
};

const registrationUser = (userObj) => ({
  type: 'REGISTRATION_USER',
  payload: userObj,
});

export { fetchUser };
