import React from 'react';
import { Catigories, PizzaBlock, SortPopup } from '../components';
import { useSelector, useDispatch } from 'react-redux';
import { setCategory, setSortBy } from '../redux/actions/filters';
import { fetchPizzas } from '../redux/actions/pizzas';
import ContentLoader from 'react-content-loader';
import { addCartPizza } from '../redux/actions/cart';

const categoryNames = ['Мужские', 'Женские', 'Детские'];

const sortItem = [
  { name: 'цене', type: 'price' },
  { name: 'алфавиту', type: 'title' },
];

function Home() {
  const dispatch = useDispatch();

  const { items, isLoaded } = useSelector(({ pizzasReducer }) => {
    return { items: pizzasReducer.items, isLoaded: pizzasReducer.isLoaded };
  });

  const { category, sortBy } = useSelector(({ filterReducer }) => filterReducer);
  const cartItems = useSelector(({ cart }) => cart.items);
  console.log(cartItems);
  React.useEffect(() => {
    dispatch(fetchPizzas(category, sortBy));
  }, [category, sortBy]);

  function onSelectCategory(index) {
    dispatch(setCategory(index));
  }

  function onSelectSortBy(type) {
    dispatch(setSortBy(type));
  }

  function loadedBlock(key) {
    return (
      <ContentLoader
        className="pizza-block"
        key={key}
        speed={1}
        width={280}
        height={460}
        viewBox="0 0 280 460"
        backgroundColor="#f3f3f3"
        foregroundColor="#ecebeb">
        <circle cx="140" cy="140" r="140" />
        <rect x="2" y="290" rx="3" ry="3" width="280" height="27" />
        <rect x="1" y="331" rx="3" ry="3" width="280" height="59" />
        <rect x="2" y="411" rx="3" ry="3" width="76" height="34" />
        <rect x="123" y="412" rx="3" ry="3" width="161" height="34" />
      </ContentLoader>
    );
  }

  function addPizzaToCart(obj) {
    dispatch(addCartPizza(obj));
  }

  return (
    <div className="container">
      <div className="content__top">
        <Catigories
          onClickItem={onSelectCategory}
          items={categoryNames}
          activeCategory={category}
        />
        <SortPopup onClickSortBy={onSelectSortBy} items={sortItem} sortBy={sortBy} />
      </div>
      <h2 className="content__title">Ассортимент</h2>
      <div className="content__items">
        {isLoaded
          ? items.map((item) => (
              <PizzaBlock
                onAddPizza={addPizzaToCart}
                addedCount={cartItems[item.id] && cartItems[item.id].length}
                key={item.id}
                {...item}
              />
            ))
          : Array(12)
              .fill(0)
              .map((_, index) => loadedBlock(index))}
      </div>
    </div>
  );
}

// Home.propTypes = {
//   items: PropTypes.arrayOf(PropTypes.object).isRequired,
// };

export default Home;
