import React from 'react';
import * as yup from 'yup';
import { Formik } from 'formik';
import { useDispatch, useSelector } from 'react-redux';
import { Link, Redirect } from 'react-router-dom';

import { signUpUser, signUpUserSuccess, signUpUserFailure } from '../redux/actions/user';
import { useHistory } from 'react-router-dom';

export default function SingUp() {
  const dispatch = useDispatch();
  const { error, isAuth } = useSelector(({ userReducer }) => userReducer);

  const history = useHistory();

  if (isAuth) return <Redirect to="/account" />;

  const validationSchema = yup.object().shape({
    username: yup.string().required('Обезятельно'),
    last_name: yup.string().required('Обезятельно'),
    password: yup.string().required('Обезятельно'),
    email: yup.string().email('Введите коректный Email').required('Обезятельно'),
  });

  const onHandleSumbit = (values) => {
    dispatch(signUpUser(values))
      .then(({ data }) => {
        localStorage.setItem('token', JSON.stringify(data.tokens));
        dispatch(signUpUserSuccess(data));
        history.push('./account');
      })
      .catch((error) => {
        if (error.response) {
          dispatch(signUpUserFailure(Object.values(error.response.data)[0]));
          console.log(error.response.data);
        } else if (error.request) {
          console.log(error.request);
        } else {
          console.log('Error', error.message);
          dispatch(signUpUserFailure(error.message));
        }
      });
  };

  return (
    <div className="container">
      <div style={{ display: 'flex' }}>
        <p style={{ marginRight: '10px' }}>Регистрация</p>
        <Link to="/login">Авторизация</Link>
      </div>
      <Formik
        initialValues={{
          username: '',
          last_name: '',
          password: '',
          email: '',
        }}
        validateOnBlur
        validationSchema={validationSchema}
        onSubmit={(values) => onHandleSumbit(values)}>
        {({ values, errors, touched, handleBlur, handleChange, handleSubmit, isValid, dirty }) => (
          <div className="form">
            <p>Имя</p>
            <input
              name="username"
              type="text"
              onChange={handleChange}
              onBlur={handleBlur}
              value={values.username}
            />{' '}
            <br />
            {touched.username && errors.username && <p className="error">{errors.username}</p>}
            <br />
            <p>Фамилия </p>
            <input
              name="last_name"
              type="text"
              onChange={handleChange}
              onBlur={handleBlur}
              value={values.last_name}
            />{' '}
            <br />
            {touched.last_name && errors.last_name && <p className="error">{errors.last_name}</p>}
            <p>Пароль </p>
            <input
              name="password"
              type="password"
              onChange={handleChange}
              onBlur={handleBlur}
              value={values.password}
            />
            {touched.password && errors.password && <p className="error">{errors.password}</p>}
            <p>Email</p>
            <input
              name="email"
              type="email"
              onChange={handleChange}
              onBlur={handleBlur}
              value={values.email}
            />
            <br />
            {touched.email && errors.email && <p className="error">{errors.email}</p>}
            <p className="error">{error}</p>
            <button disabled={!isValid && !dirty} onClick={handleSubmit} type="submit">
              Отправить
            </button>
          </div>
        )}
      </Formik>
    </div>
  );
}
