import React from 'react';
import './scss/app.scss';
import { Route, Redirect } from 'react-router-dom';
import { Header } from './components';
import { Cart, Home, Registration, Account, Edit } from './pages';
import { meFromToken } from './redux/actions/reload';

import { useDispatch, useSelector } from 'react-redux';

function App() {
  const dispatch = useDispatch();
  const { isAuth } = useSelector((registration) => registration);
  React.useEffect(() => {
    let token = JSON.parse(localStorage.getItem('token'));

    if (!token || token === '') {
      return;
    }

    dispatch(meFromToken(token.access));

    // eslint-disable-next-line
  }, []);

  return (
    <div className="wrapper">
      <Header />
      <div className="content">
        <Route exact path="/">
          {/* <Route exact path="/" component={Home} /> */}
          {isAuth ? <Redirect to="/account" /> : <Home />}
          <Route exact path="/cart" component={Cart} />
          <Route exact path="/registration" component={Registration} />
          <Route exact path="/account" component={Account} />
          <Route exact path="/edit" component={Edit} />
        </Route>
      </div>
    </div>
  );
}

export default App;
