import React from 'react';
import * as yup from 'yup';
import { Formik } from 'formik';
import { useDispatch } from 'react-redux';

import { fetchUser } from '../redux/actions/registration';

function Registration() {
  const dispatch = useDispatch();

  const validationSchema = yup.object().shape({
    name: yup.number().typeError('aaaaaa').required('ddd'),
  });

  const ffhandleSubmit = (event) => {
    event.preventDefault();
  };

  return (
    <div>
      <Formik
        initialValues={{
          name: '',
          secondName: '',
          password: '',
          confirmPassword: '',
          email: '',
        }}
        validateOnBlur
        validationSchema={validationSchema}
        onSubmit={ffhandleSubmit}>
        {({ values, errors, touched, handleBlur, handleChange, handleSubmit, isValid, dirty }) => (
          <div className="form">
            <p>Name</p>
            <input
              name="name"
              type="text"
              onChange={handleChange}
              onBlur={handleBlur}
              value={values.name}
            />
            {touched.name && errors.name && <p className="input">{errors.name}</p>}
            <button disabled={!isValid && !dirty} onClick={handleSubmit} type="submit">
              Отправить
            </button>
          </div>
        )}
      </Formik>
    </div>
  );
}

export default Registration;
