import axios from 'axios';

const fetchUser = (user) => (dispatch) => {
  axios
    .post(`https://jastegri.pythonanywhere.com/auth/users/`, user)
    .then(({ data }) => {
      console.log(data);
      localStorage.setItem('token', JSON.stringify(data.tokens));
      dispatch(registrationUser(data));
    })
    .catch((err) => console.log(err.message));
};

const registrationUser = (userObj) => ({
  type: 'REGISTRATION_USER',
  payload: userObj,
});

export { fetchUser };

// https://github.com/rajaraodv/react-redux-blog
