import axios from 'axios';
const url = 'https://jastegri.pythonanywhere.com';

export const signUpUser = (user) => (dispatch) => {
  dispatch(signUpUserStarted());

  return axios.post(`${url}/auth/users/`, user);
};

const signUpUserStarted = () => ({
  type: 'SIGNUP_USER',
});

export const signUpUserSuccess = (user) => ({
  type: 'SIGNUP_USER_SUCCESS',
  payload: user,
});

export const signUpUserFailure = (error) => ({
  type: 'SIGNUP_USER_FAILURE',
  payload: error,
});

export const meFromToken = (tokenFromStorage) => (dispatch) => {
  dispatch(meFromTokenStarted());

  axios
    .get(`${url}/auth/users/me/`, {
      headers: { Authorization: `Bearer ${tokenFromStorage}` },
    })
    .then((res) => {
      // localStorage.setItem('token', res.data.tokens);
      dispatch(meFromTokenSuccess(res.data));
    })
    .catch((err) => {
      localStorage.removeItem('token');
      dispatch(meFromTokenFailure(err.message));
    });
};

const meFromTokenStarted = () => ({
  type: 'ME_FROM_TOKEN',
});

const meFromTokenSuccess = (user) => ({
  type: 'ME_FROM_TOKEN_SUCCESS',
  payload: user,
});

const meFromTokenFailure = (error) => ({
  type: 'ME_FROM_TOKEN_FAILURE',
  payload: error,
});

export const loginUser = (user) => (dispatch) => {
  dispatch(loginUserStarted());

  axios
    .post(`${url}/login/`, user)
    .then(({ data }) => {
      const tokens = {
        refresh: data.refresh,
        access: data.access,
      };

      localStorage.setItem('token', JSON.stringify(tokens));
      dispatch(loginUserSuccess(data));
    })
    .catch((err) => {
      dispatch(loginUserFailure(err.message));
    });
};

const loginUserStarted = () => ({
  type: 'LOGIN_USER',
});

const loginUserSuccess = (user) => ({
  type: 'LOGIN_USER_SUCCESS',
  payload: user,
});

const loginUserFailure = (error) => ({
  type: 'LOGIN_USER_FAILURE',
  payload: error,
});

export const editUser = (user, id) => (dispatch) => {
  dispatch(editUserStarted());

  // console.log(obj);
  axios
    .put(`${url}/update-profile/${id}/`, {
      username: user.username,
      last_name: user.last_name,
      email: user.email,
    })
    .then(({ data }) => {
      // localStorage.setItem('token', JSON.stringify(data.tokens));
      dispatch(editUserSuccess(data));
    })
    .catch((err) => {
      dispatch(editUserFailure(err.message));
    });
};

const editUserStarted = (data) => ({
  type: 'EDIT_USER',
  payload: data,
});

const editUserSuccess = (user) => ({
  type: 'EDIT_USER_SUCCESS',
  payload: user,
});

const editUserFailure = (error) => ({
  type: 'EDIT_USER_FAILURE',
  payload: error,
});

export const logoutUser = () => ({
  type: 'LOGOUT_USER',
});
