import React from 'react';
import { useHistory } from 'react-router-dom';
import { useForm } from 'react-hook-form';
import { useSelector, useDispatch } from 'react-redux';

import 'react-loader-spinner/dist/loader/css/react-spinner-loader.css';
import Loader from 'react-loader-spinner';

import { editUser, editUserFailure, editUserSuccess } from '../redux/actions/user';
import { Alert, ChangePass } from '../components';

function Edit() {
  const { loading, user } = useSelector(({ userReducer }) => userReducer);
  const history = useHistory();
  const [clicked, setClickedError] = React.useState(false);
  const dispatch = useDispatch();
  const { register, errors, handleSubmit } = useForm();

  const onSubmit = (values) => {
    const token = JSON.parse(localStorage.getItem('token'));
    dispatch(editUser(values, user.id, token))
      .then(({ data }) => {
        dispatch(editUserSuccess(data));
        history.push('/account');
      })
      .catch((err) => {
        if (err.request) {
          dispatch(editUserFailure(JSON.parse(err.request.response)));
          setClickedError(true);
        }
      });
  };

  return (
    <div className="container">
      <h3 style={{ marginBottom: '20px' }}>Редоктировать </h3>
      {loading ? (
        <Loader visible={true} type="Circles" color="#000" height={80} width={80} />
      ) : (
        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
          <form onSubmit={handleSubmit(onSubmit)}>
            <div className="form-control">
              <input
                className="inp"
                type="text"
                name="username"
                defaultValue={user.username}
                ref={register({ required: 'Username is required.' })}
              />
              {errors.username && <p className="error"> {errors.username.message} </p>}
            </div>
            <div className="form-control">
              <input
                className="inp"
                type="number"
                name="phone"
                placeholder="Телефон"
                defaultValue={''}
                ref={register({ required: 'Phone is required.' })}
              />
              {errors.phone && <p className="error"> {errors.phone.message} </p>}
            </div>
            <div className="form-control">
              <input
                className="inp"
                type="text"
                name="last_name"
                defaultValue={user.last_name}
                ref={register({ required: 'last_name is required.' })}
              />
              {errors.last_name && <p className="error"> {errors.last_name.message} </p>}
            </div>
            <div className="form-control">
              <input
                className="inp"
                type="text"
                name="email"
                defaultValue={user.email}
                ref={register({
                  required: 'Email is required.',
                  pattern: {
                    value: /^[^@ ]+@[^@ ]+\.[^@ .]{2,}$/,
                    message: 'Email is not valid.',
                  },
                })}
              />
              {errors.email && <p className="error"> {errors.email.message} </p>}
            </div>
            {/* {clicked ? <Alert /> : ''} */}

            <div className="form-control">
              <button className="form__btn btn" type="submit">
                Сохранить
              </button>
            </div>
          </form>
          <ChangePass id={user.id} />
        </div>
      )}
    </div>
  );
}

export default Edit;
