import axios from 'axios';

const fetchUser = (user) => (dispatch) => {
  axios.post(`https://jastegri.pythonanywhere.com/auth/users/`, { user }).then(({ data }) => {
    localStorage.setItem('token', data.jwt);
    dispatch(regisUser(data));
  });
};

const regisUser = (userObj) => ({
  type: 'REGIS_USER',
  payload: userObj,
});

export { fetchUser };
