import axios from 'axios';
import {} from 'redux-thunk';
const fetchPizzas = () => {
  axios.get('http://localhost:3001/pizzas').then(({ data }) => {
    setPizzas(data);
  });
};

const setPizzas = (items) => ({
  type: 'SET_PIZZAS',
  payload: items,
});

export { setPizzas, fetchPizzas };
