import React from 'react';
import { Catigories, PizzaBlock, SortPopup } from '../components';
import { useSelector, useDispatch } from 'react-redux';
// import PropTypes, { func } from 'prop-types';
import { setCategory } from '../redux/actions/filters';
import { fetchPizzas } from '../redux/actions/pizzas';

const categoryNames = ['Мясные', 'Вегетарианская', 'Гриль', 'Острые', 'Закрытые'];
const sortItem = [
  { name: 'популярности', type: 'popular' },
  { name: 'цене', type: 'price' },
  { name: 'алфавиту', type: 'alphabet' },
];

function Home() {
  const dispatch = useDispatch();
  const items = useSelector(({ pizzasReducer }) => pizzasReducer.items);

  React.useEffect(() => {
    // if (items.length) {
    dispatch(fetchPizzas());
    // }
  }, []);

  function onSelectCategory(index) {
    dispatch(setCategory(index));
  }

  return (
    <div className="container">
      <div className="content__top">
        <Catigories onClickItem={onSelectCategory} items={categoryNames} />
        <SortPopup items={sortItem} />
      </div>
      <h2 className="content__title">Все пиццы</h2>
      <div className="content__items">
        {items && items.map((item) => <PizzaBlock key={item.id} {...item} />)}
      </div>
    </div>
  );
}

// Home.propTypes = {
//   items: PropTypes.arrayOf(PropTypes.object).isRequired,
// };

export default Home;
