import React from 'react';
import { Formik } from 'formik';
import * as yup from 'yup';

import { useSelector, useDispatch } from 'react-redux';
import { loginUser, loginUserFailure, loginUserSuccess } from '../redux/actions/user';
import { Link, Redirect, useHistory } from 'react-router-dom';

function Login() {
  const { error, isAuth, loading } = useSelector(({ userReducer }) => userReducer);

  const dispatch = useDispatch();

  const history = useHistory();

  const validationSchema = yup.object().shape({
    username: yup.string().required('Обезятельно'),
    password: yup.string().required('Обезятельно'),
  });

  const onHandleSumbit = (values) => {
    dispatch(loginUser(values))
      .then(({ data }) => {
        localStorage.setItem('token', JSON.stringify(data.authToken));
        dispatch(loginUserSuccess(data));
        history.push('/account');
      })
      .catch((err) => {
        dispatch(loginUserFailure(err.message));
      });
  };

  return (
    <div className="container">
      <div style={{ display: 'flex' }}>
        <Link to="/signup" style={{ marginRight: '10px' }}>
          Регистрация
        </Link>
        <Link to="/login">Авторизация</Link>
      </div>
      <Formik
        initialValues={{
          username: '',
          password: '',
        }}
        validateOnBlur
        validationSchema={validationSchema}
        onSubmit={(values) => onHandleSumbit(values)}>
        {({ values, errors, touched, handleBlur, handleChange, handleSubmit, isValid, dirty }) => (
          <div className="form">
            <p>Имя</p>
            <input
              name="username"
              type="text"
              onChange={handleChange}
              onBlur={handleBlur}
              value={values.username}
            />{' '}
            <br />
            {touched.username && errors.username && <p className="error">{errors.username}</p>}
            <br />
            {touched.last_name && errors.last_name && <p className="error">{errors.last_name}</p>}
            <p>Пароль </p>
            <input
              name="password"
              type="password"
              onChange={handleChange}
              onBlur={handleBlur}
              value={values.password}
            />
            <p className="error">{error}</p>
            <button disabled={!isValid && !dirty} onClick={handleSubmit} type="submit">
              Войти
            </button>
          </div>
        )}
      </Formik>
    </div>
  );
}

export default Login;
