import React from 'react';

function CartItem({ pizza, totalPrice, totalCount, onRemove, incCount, decCount }) {
  const deleteItem = (id) => {
    onRemove(pizza.id);
  };

  const onIncCount = () => {
    incCount(pizza.id);
  };

  const onDecCount = () => {
    decCount(pizza.id);
  };

  return (
    <div className="cart__item">
      <div className="cart__item-img">
        <img className="pizza-block__image" src={pizza.img} alt="Pizza" />
      </div>
      <div className="cart__item-info">
        <h3> {pizza.title} </h3>
      </div>
      <div className="cart__item-count">
        <div onClick={onDecCount} className="button--cirkule cart__item-count-minus">
          -
        </div>
        <b>{totalCount}</b>
        <div onClick={onIncCount} className="cart__item-count-plus">
          +
        </div>
      </div>
      <div className="cart__item-price">
        <b>{totalPrice} USD</b>
      </div>
      <div className="cart__item-remove">
        <div onClick={deleteItem}>Удалить</div>
      </div>
    </div>
  );
}

export default CartItem;
