import axios from 'axios';

const meFromToken = (tokenFromStorage) => (dispatch) => {
  return axios
    .get(`https://jastegri.pythonanywhere.com/auth/users/me/`, {
      headers: { Authorization: `Bearer ${tokenFromStorage}` },
    })
    .then(({ data }) => {
      // localStorage.setItem('token', JSON.stringify(data.tokens));

      dispatch(reloadToken(data));
    })
    .catch((err) => console.log(err.message));
};

const reloadToken = (data) => ({
  type: 'ME_FROM_TOKEN',
  payload: data,
});

export { meFromToken };

