import React from 'react';
import { useForm } from 'react-hook-form';
import { useDispatch, useSelector } from 'react-redux';
import {
  changePassword,
  changePasswordFailure,
  changePasswordSuccess,
} from '../redux/actions/user';
import userReducer from '../redux/reducers/user';

function ChangePass({ id }) {
  const { register, errors, handleSubmit, watch } = useForm();
  const { user } = useSelector(({ userReducer }) => userReducer);
  const dispatch = useDispatch();
  // console.log(user.password);

  const pass = React.useRef({});
  pass.current = watch('password', '');

  const onChangePassword = (values) => {
    const token = JSON.parse(localStorage.getItem('token'));
    console.log(values);
    console.log(values.old_password);
    dispatch(changePassword(values, id, token))
      .then(({ data }) => {
        dispatch(changePasswordSuccess(data));
      })
      .catch((err) => {
        dispatch(changePasswordFailure(err.message));
      });
  };

  return (
    <form className="form" onSubmit={handleSubmit(onChangePassword)}>
      <div className="form-control">
        <label>Ваш пароль </label>
        <input
          name="old_password"
          type="password"
          defaultValue={user.password}
          ref={register({
            required: 'Password is required.',
            minLength: {
              value: 8,
              message: 'Password should be at-least 6 characters.',
            },
          })}
        />
        {errors.old_password && <p className="error">{errors.old_password.message}</p>}
      </div>
      <div className="form-control">
        <p>Введите новый пароль </p>
        <input
          name="password"
          type="password"
          ref={register({
            required: 'last_name is required.',
            minLength: {
              value: 8,
              message: 'Password should be at-least 6 characters.',
            },
          })}
        />
        {errors.password && <p className="error">{errors.password.message}</p>}
      </div>
      <div className="form-control">
        <p>Повторите пароль </p>
        <input
          name="password2"
          type="password"
          ref={register({
            validate: (value) => value === pass.current || 'Пароли не совподают',
            required: 'last_name is required.',
            minLength: {
              value: 6,
              message: 'Password should be at-least 6 characters.',
            },
          })}
        />
        {errors.password2 && <p className="error">{errors.password2.message}</p>}
      </div>
      <button type="submit">Сохранить</button>
    </form>
  );
}

export default ChangePass;
