import React from 'react';

import { useSelector, useDispatch } from 'react-redux';

import {
  changePassword,
  changePasswordFailure,
  changePasswordSuccess,
  editUser,
  editUserFailure,
  editUserSuccess,
} from '../redux/actions/user';
import { useHistory } from 'react-router-dom';
import { useForm } from 'react-hook-form';

function Edit() {
  const { loading, isAuth, user } = useSelector(({ userReducer }) => userReducer);
  const dispatch = useDispatch();
  const history = useHistory();
  const { register, errors, handleSubmit } = useForm();

  const onSave = (values, id) => {
    const token = JSON.parse(localStorage.getItem('token'));
    dispatch(editUser(values, id, token))
      .then(({ data }) => {
        dispatch(editUserSuccess(data));
        history.push('/account');
        console.log(data);
      })
      .catch((err) => {
        dispatch(editUserFailure(err.message));
      });
  };

  const onChangePassword = (values, id) => {
    const token = JSON.parse(localStorage.getItem('token'));
    console.log(token, ' Cliked password save');
    dispatch(changePassword(values, id, token))
      .then(({ data }) => {
        dispatch(changePasswordSuccess(data));
      })
      .catch((err) => {
        dispatch(changePasswordFailure(err.message));
      });
  };

  return (
    <div className="container">
      <h3 style={{ marginBottom: '20px' }}>Редоктировать </h3>
      {loading ? (
        <div>Loading...</div>
      ) : (
        <div style={{ display: 'flex' }}>
          <form onSubmit={handleSubmit(onSubmit)}>
            <div className="form-control">
              <label>Username</label>
              <input
                type="text"
                name="username"
                ref={register({ required: 'Username is required.' })}
              />
              <div className="form-control">
                {errors.username && <p className="error"> {errors.username.message} </p>}
              </div>
              <label>Фамилия</label>
              <input
                type="text"
                name="last_name"
                ref={register({ required: 'last_name is required.' })}
              />
              {errors.last_name && <p className="error"> {errors.last_name.message} </p>}
            </div>
            <div className="form-control ">
              <label>Email</label>
              <input
                type="text"
                name="email"
                ref={register({
                  required: 'Email is required.',
                  pattern: {
                    value: /^[^@ ]+@[^@ ]+\.[^@ .]{2,}$/,
                    message: 'Email is not valid.',
                  },
                })}
              />
              {errors.email && <p className="error"> {errors.email.message} </p>}
            </div>
            <div className="form-control">
              <label>Password</label>
              <input
                type="password"
                name="password"
                ref={register({
                  required: 'Password is required.',
                  minLength: {
                    value: 6,
                    message: 'Password should be at-least 6 characters.',
                  },
                })}
              />
              {errors.password && <p className="error"> {errors.password.message} </p>}
            </div>
            <div className="form-control">
              <label></label>
              <button type="submit">Регистрация</button>
            </div>
          </form>
          <form className="form">
            <div className="form-control">
              <label>Ваш пароль </label>
              <input name="old_password" type="password" />
              {errors.old_password && <p className="error">{errors.old_password.message}</p>}
            </div>
            <div className="form-control">
              <p>Введите новый пароль </p>
              <input name="password" type="password" />
              {errors.password && <p className="error">{errors.password.message}</p>}
            </div>
            <div className="form-control">
              <p>Повторите пароль </p>
              <input name="password2" type="password" />
              {errors.password2 && <p className="error">{errors.password2.message}</p>}
            </div>
            <button type="submit">Сохранить</button>
          </form>
        </div>
      )}
    </div>
  );
}

export default Edit;
