import React from 'react';
import { Formik } from 'formik';
import * as yup from 'yup';
import { Link } from 'react-router-dom';

import { useDispatch, useSelector } from 'react-redux';

import { useHistory } from 'react-router-dom';

import personalAreaImg from '../assets/img/personal.png';
import { logoutUser, loginUser, loginUserFailure, loginUserSuccess } from '../redux/actions/user';

function LoginPopup() {
  const { user, isAuth } = useSelector(({ userReducer }) => userReducer);
  const [visiblePopup, setVisiblePopup] = React.useState(false);

  const dispatch = useDispatch();
  const history = useHistory();

  const validationSchema = yup.object().shape({
    username: yup.string().required('Обезятельно'),
    password: yup.string().required('Обезятельно'),
  });

  const onHandleSumbit = (values) => {
    dispatch(loginUser(values))
      .then(({ data }) => {
        localStorage.setItem('token', JSON.stringify(data.auth_token));
        dispatch(loginUserSuccess(data));
        setVisiblePopup(false);
        history.push('/account');
      })
      .catch((err) => {
        dispatch(loginUserFailure(err.message));
      });
  };

  const logoutButton = () => {
    localStorage.removeItem('token');
    dispatch(logoutUser());
    setVisiblePopup(false);
    history.push('/');
  };

  const loginRef = React.useRef();

  const toggleVisiblePopup = () => {
    setVisiblePopup(!visiblePopup);
  };

  const handleOutsideClick = (event) => {
    if (!event.path.includes(loginRef.current)) {
      setVisiblePopup(false);
    }
  };

  React.useEffect(() => {
    document.body.addEventListener('click', handleOutsideClick);
  }, []);

  return (
    <div className="personal" ref={loginRef}>
      <img onClick={toggleVisiblePopup} width="50px" alt="img-login" src={personalAreaImg} />
      {visiblePopup ? (
        <div className="popup">
          {isAuth ? (
            <div className="popup">
              <div className="popup__list">
                <Link to="/account">Личный кабинет</Link>
                <Link to="/edit">Редоктировать</Link>
                <button onClick={logoutButton}> Выйти </button>
              </div>
            </div>
          ) : (
            <Formik
              initialValues={{
                username: '',
                password: '',
              }}
              validateOnBlur
              validationSchema={validationSchema}
              onSubmit={(values) => onHandleSumbit(values)}>
              {({
                values,
                errors,
                touched,
                handleBlur,
                handleChange,
                handleSubmit,
                isValid,
                dirty,
              }) => (
                <div className="popup__list">
                  <p>Username</p>
                  <input
                    name="username"
                    type="text"
                    onChange={handleChange}
                    onBlur={handleBlur}
                    value={values.username}
                  />
                  <br />
                  {touched.username && errors.username && (
                    <p className="error">{errors.username}</p>
                  )}

                  <p>Пароль </p>
                  <input
                    name="password"
                    type="password"
                    onChange={handleChange}
                    onBlur={handleBlur}
                    value={values.password}
                  />
                  {touched.password && errors.password && (
                    <p className="error">{errors.password}</p>
                  )}

                  <button disabled={!isValid && !dirty} onClick={handleSubmit} type="submit">
                    Войти
                  </button>
                  <p>
                    Впервые у нас? <Link to="/signup">Зарегистрироваться</Link>
                  </p>
                </div>
              )}
            </Formik>
          )}
        </div>
      ) : (
        ''
      )}
    </div>
  );
}

export default LoginPopup;
