import React from 'react';
import * as yup from 'yup';
import { Formik } from 'formik';
import { useSelector } from 'react-redux';

function Edit() {
  // const dispatch = useDispatch();
  const { userName, lastName, email } = useSelector(({ registration }) => registration.currentUser);

  const validationSchema = yup.object().shape({
    username: yup.string().required('Обезятельно'),
    last_name: yup.string().required('Обезятельно'),
    password: yup.string().required('Обезятельно'),
    email: yup.string().email('Введите коректный Email').required('Обезятельно'),
  });

  const onSave = (values) => {
    console.log('This data save');
    // dispatch(fetchUser(values));
  };

  return (
    <div>
      <Formik
        initialValues={{
          username: userName,
          last_name: lastName,
          password: 'fsafafa',
          email,
        }}
        validateOnBlur
        validationSchema={validationSchema}
        onSubmit={(values) => onSave(values)}>
        {({ values, errors, touched, handleBlur, handleChange, handleSubmit, isValid, dirty }) => (
          <div className="form">
            <p>Имя</p>
            <input
              name="username"
              type="text"
              onChange={handleChange}
              onBlur={handleBlur}
              value={values.username}
            />{' '}
            <br />
            {touched.username && errors.username && <p className="error">{errors.username}</p>}
            <br />
            <p>Фамилия </p>
            <input
              name="last_name"
              type="text"
              onChange={handleChange}
              onBlur={handleBlur}
              value={values.last_name}
            />{' '}
            <br />
            {touched.last_name && errors.last_name && <p className="error">{errors.last_name}</p>}
            <p>Пароль </p>
            <input
              name="password"
              type="password"
              onChange={handleChange}
              onBlur={handleBlur}
              value={values.password}
            />
            {touched.password && errors.password && <p className="error">{errors.password}</p>}
            <p>Email</p>
            <input
              name="email"
              type="email"
              onChange={handleChange}
              onBlur={handleBlur}
              value={values.email}
            />
            <br />
            {touched.email && errors.email && <p className="error">{errors.email}</p>}
            <button disabled={!isValid && !dirty} onClick={onSave} type="submit">
              Сохранить
            </button>
          </div>
        )}
      </Formik>
    </div>
  );
}

export default Edit;
