const initialState = {
  items: {},
  totalPrice: 0,
  itemCount: 0,
};

function cart(state = initialState, action) {
  if (action.type === 'SET_TOTALPRICE') {
    return {
      ...state,
      totalPrice: action.payload,
    };
  }

  if (action.type === 'SET_COUNT') {
    return {
      ...state,
      itemCount: action.payload,
    };
  }

  switch (action.type) {
    case 'ADD_PIZZAS':
      const newItems = {
        ...state.items,
        [action.payload.id]: !state.items[action.payload.id]
          ? [action.payload]
          : [...state.items[action.payload.id], action.payload],
      };

      const totalArr = [].concat.apply([], Object.values(newItems));
      return {
        ...state,
        items: newItems,
        itemCount: totalArr.length,
        // totalPrice: totalArr,
      };
    default:
      return state;
  }
}

export default cart;

const arr = [
  { price: 1 },
  { price: 20 },
  { price: 10 },
  { price: 30 },
  { price: 50 },
  { price: 7 },
  { price: 780 },
  { price: 33 },
];
