import React from 'react';
import { Link } from 'react-router-dom';

const TabAuth = () => {
  const toggleClassBtn = (event) => {
    const tabs = document.querySelectorAll('.auth-tab__link');

    tabs.forEach((item) => {
      item.classList.remove('active');
    });

    if (item === event.target) {
      item.classList.add('active');
    }
  };

  return (
    <div onClick={toggleClassBtn} className="auth-tab">
      <Link className="auth-tab__link active" to="/signup">
        Регистрация
      </Link>
      <Link className="auth-tab__link" to="/login">
        Авторизация
      </Link>
    </div>
  );
};

export default TabAuth;
