import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';

import logo from '../assets/img/logo.png';
import cartImg from '../assets/img/cart.png';
import searchImg from '../assets/img/search.png';

import { Button, LoginPopup } from '../components';
import { fetchPizzas } from '../redux/actions/pizzas';
import { setCategory } from '../redux/actions/filters';

function Header() {
  const { itemCount } = useSelector(({ cart }) => cart);
  const cartItems = useSelector(({ cart }) => cart.items);
  const { category } = useSelector(({ filterReducer }) => filterReducer);

  const dispatch = useDispatch();

  const categoryNames = ['Мужские', 'Женские', 'Детские'];

  React.useEffect(() => {
    dispatch(fetchPizzas(category));

    // eslint-disable-next-line
  }, [category]);

  function onSelectCategory(index) {
    dispatch(setCategory(index));
  }

  return (
    <header class="header">
      <div class="container">
        <nav class="nav">
          <Link to="/" class="nav__logo">
            <img src={logo} alt="" />
          </Link>

          <Catigories
            onClickItem={onSelectCategory}
            items={categoryNames}
            activeCategory={category}
          />

          <div class="nav__control">
            <button class="search-button">
              <img src={searchImg} alt="" />
            </button>
            <Link to="/cart" class="cart-button">
              <img src={cartImg} alt="" />
              <span class="cart-count">{itemCount}</span>
            </Link>
            <LoginPopup />
          </div>
        </nav>
      </div>
    </header>
  );
}

export default Header;

