import React from 'react';
import * as yup from 'yup';
import { Formik } from 'formik';
import { useDispatch } from 'react-redux';

import { fetchUser } from '../redux/actions/registration';

function Registration() {
  const dispatch = useDispatch();
  const initState = {
    username: '',
    password: '',
  };

  const validationScheme = yup.object().shape({
    name: yup.string().isRe,
  });

  const [state, setState] = React.useState(initState);

  const handleChange = (event) => {
    setState({ [event.target.name]: event.target.value });
    // console.log(initState);
  };

  const ffhandleSubmit = (event) => {
    event.preventDefault();
    console.log(state);
  };

  // const regisHandler = (event) => {
  //   console.log({ username, password });

  //   dispatch(fetchUser({ username, password }));
  // };

  return (
    <div>
      <Formik
        initialValues={{
          name: '',
          secondName: '',
          password: '',
          confirmPassword: '',
          email: '',
        }}
        validateOnBlur
        onSubmit={ffhandleSubmit}>
        {({ values, errors, touched, handleBlur, handleChange, handleSubmit, isValid, dirty }) => (
          <div className="form">
            <p>Name</p>
            <input
              name="name"
              type="text"
              onChange={handleChange}
              onBlur={handleBlur}
              value={values.name}
            />
            {touched.name && errors.name && <p className="input">{errors.name}</p>}
            <button disabled={!isValid && !dirty} onClick={handleSubmit} type="submit"></button>
          </div>
        )}
      </Formik>
    </div>
  );
}

export default Registration;
