function addCartPizza(pizzaObj) {
  return {
    type: 'ADD_PIZZAS',
    payload: pizzaObj,
  };
}

function clearCart() {
  return {
    type: 'CLEAR_CART',
  };
}

function removeCartItem(id) {
  return {
    type: 'REMOVE_CART_ITEM',
    payload: id,
  };
}

export { addCartPizza, clearCart, removeCartItem };
