import React from 'react';
import * as yup from 'yup';
import { Formik } from 'formik';
import { useDispatch } from 'react-redux';

import { fetchUser } from '../redux/actions/registration';

function Registration() {
  const dispatch = useDispatch();

  const validationSchema = yup.object().shape({
    name: yup.string().required('Обезятельно'),
    secondName: yup.string().required('Обезятельно'),
    password: yup.string().required('Обезятельно'),
    confirmPassword: yup
      .string()
      .oneOf([yup.ref('password')], 'Пароли не совпадают')
      .required('Обезятельно'),
    email: yup.string().email('Введите коректный Email').required('Обезятельно'),
  });

  const onHandleSumbit = (values) => {
    // event.preventDefault();
    console.log(values);
    dispatch(fetchUser(values));
  };

  return (
    <div>
      <Formik
        initialValues={{
          name: '',
          secondName: '',
          password: '',
          confirmPassword: '',
          email: '',
        }}
        validateOnBlur
        validationSchema={validationSchema}
        onSubmit={(values) => onHandleSumbit(values)}>
        {({ values, errors, touched, handleBlur, handleChange, handleSubmit, isValid, dirty }) => (
          <div className="form">
            <p>Имя</p>
            <input
              name="name"
              type="text"
              onChange={handleChange}
              onBlur={handleBlur}
              value={values.name}
            />{' '}
            <br />
            {touched.name && errors.name && <p className="input">{errors.name}</p>}
            <br />
            <p>Фамилия </p>
            <input
              name="secondName"
              type="text"
              onChange={handleChange}
              onBlur={handleBlur}
              value={values.secondName}
            />{' '}
            <br />
            {touched.secondName && errors.secondName && (
              <p className="input">{errors.secondName}</p>
            )}
            <p>Пароль </p>
            <input
              name="password"
              type="password"
              onChange={handleChange}
              onBlur={handleBlur}
              value={values.password}
            />
            <p>Повторите пароль</p>
            <input
              name="confirmPassword"
              type="password"
              onChange={handleChange}
              onBlur={handleBlur}
              value={values.confirmPassword}
            />
            <br />
            {touched.confirmPassword && errors.confirmPassword && (
              <p className="error">{errors.confirmPassword}</p>
            )}
            <p>Email</p>
            <input
              name="email"
              type="email"
              onChange={handleChange}
              onBlur={handleBlur}
              value={values.email}
            />
            <br />
            {touched.email && errors.email && <p className="input">{errors.email}</p>}
            <button disabled={!isValid && !dirty} onClick={handleSubmit} type="submit">
              Отправить
            </button>
          </div>
        )}
      </Formik>
    </div>
  );
}

export default Registration;
